import scrapyfrom ..items import NewsscraperItemclass NewsScrapper(scrapy.Spider):    name = 'newsScrap'    start_urls = [        "https://www.thesun.co.uk/tvandshowbiz/10063800/elbow-guy-garvey-dark-new-album-brexit",        "https://www.thesun.co.uk/tvandshowbiz/10123336/bbc-investigation-lottie-lion/",        "https://www.thesun.co.uk/news/10122657/keep-left-signs-harry-dunn-crash-site/"    ]    def parse(self, response):        items = NewsscraperItem()        newsdiv = response.css('.article__media+ p')  # for getting title text        headline = response.css('.article__headline::text')[0].extract()        colortext = response.css('.t-p-color::text')[0].extract()        fullNews = response.css('.article__media+ p::text')[0].extract()        fullNews1 = response.css('.article__media+ p::text')[1].extract()        imageUrl = response.xpath('//*[@id="main-content"]/section/div/div[1]/article/div[2]/div[1]/figure[1]/div/a/img/@data-src').extract()        yield {            'headline': headline,            'colortext': colortext,            'fullnews': fullNews+fullNews1,            'imageUrl': imageUrl               }